import { store } from "../store";
import classes from "./Counter.module.css";
import { calculatedActionTypes } from "../store";
import { useSelector } from "react-redux";
const Counter = () => {
 const result =  useSelector((state) => {
    return state.result;
  });
  // const { result } = store.getState();
  const dispatch = 
  console.log(result);
  const addHandler = () => {
    store.dispatch({ type: calculatedActionTypes.ADD, payload: 20 });
  };
  const subtractHandler = () => {
    store.dispatch({ type: calculatedActionTypes.SUBTRACT, payload: 5 });
  };
  const multiplyHandler = () => {
    store.dispatch({ type: calculatedActionTypes.MULTIPLY, payload: 10 });
  };

  const divideHandler = () => {
    store.dispatch({ type: calculatedActionTypes.DIVIDE, payload: 9 });
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>Result: {result}</div>
      <button onClick={addHandler}>+20</button>
      <button onClick={subtractHandler}>-5</button>
      <button onClick={multiplyHandler}>*10</button>
      <button onClick={divideHandler}>/9</button>
    </main>
  );
};

export default Counter;
